import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamForm extends React.Component {

  renderError({ error, touched }){
    if(touched && error){
      return (
        <div className="ui error message">
          <div className="header">
            {error}
          </div>
        </div>
    )}
  }
  // ...formProps.input replaces value= and onChange= properties; also, formProps can be destructured.
  renderInput = (formProps) => {
    const className = `field ${formProps.meta.touched && formProps.meta.error ? 'error': ''}`;
    return (
      <div className={className}>
      <label>{formProps.label}</label>
        <input {...formProps.input} autoComplete='off' />
        {this.renderError(formProps.meta)}
      </div>
    )
  };

  onSubmit = (formValues) => {
    // Redux form takes care of e.preventDefault for us, so no need to call it.
    this.props.onSubmit(formValues);
  }

  render() {
    return (
        <form className="ui form error" onSubmit={this.props.handleSubmit(this.onSubmit)} >
          <Field name="title" component={this.renderInput} label="Enter Title" />
          <Field name="description" component={this.renderInput} label="Enter Description" />
          <button className="ui button primary">Submit</button>
        </form>
  )};
}

const validate = (formValues) => {
  const errors = {};
  if(!formValues.title){
    errors.title = "You must enter a title";
  }
  if(!formValues.description) {
    errors.description = "You must enter a description";
  }
  return errors;
};

export default reduxForm({
  form: 'streamForm',
  validate
})(StreamForm);


